        var rowCount = this.getRowSizes().length;
        var firstRow = this.getFirstRow();
        var firstColumn = this.getFirstColumn();

        if (row < firstRow || row >= firstRow + rowCount || column < firstColumn || column >= firstColumn + columnCount) {
          return null;
        }

